CREATE TABLE blog (`blogId` INT PRIMARY KEY NOT NULL, `blogTitle` VARCHAR(255)  NOT NULL CHECK (LENGTH(blogTitle) <= 255), `blogContent` VARCHAR(255)  NOT NULL CHECK (LENGTH(blogContent) <= 5000), `blogAuthor` VARCHAR(255)  NOT NULL CHECK (LENGTH(blogAuthor) <= 255), `blogImage` VARCHAR(255)  NOT NULL CHECK (LENGTH(blogImage) <= 255), `blogUpdated` TIMESTAMP  NOT NULL, `blogIdentify` VARCHAR(255)  NOT NULL)