CREATE TABLE campaign (`campaignId` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `campainTitle` VARCHAR(255)  NOT NULL CHECK (LENGTH(campainTitle) <= 255), `campaignSubtitle` VARCHAR(255)  NOT NULL CHECK (LENGTH(campaignSubtitle) <= 255), `campaignMedia` VARCHAR(255)  NOT NULL CHECK (LENGTH(campaignMedia) <= 255), `campaignUpdated` TIMESTAMP  NOT NULL, `campaignIdentify` VARCHAR(255)  NOT NULL)