CREATE TABLE clients (`clientId` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `clientName` VARCHAR(255)  NOT NULL CHECK (LENGTH(clientName) <= 255), `clientEmail` VARCHAR(255)  NOT NULL CHECK (LENGTH(clientEmail) <= 255), `whatsapp` VARCHAR(255)  NOT NULL CHECK (LENGTH(whatsapp) <= 255), `clientMessage` VARCHAR(255)  NOT NULL CHECK (LENGTH(clientMessage) <= 50000), `clientUpdated` TIMESTAMP  NOT NULL, `clientIdentify` VARCHAR(255)  NOT NULL)