CREATE TABLE customers (`customerId` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `cutomerName` VARCHAR(255)  NOT NULL, `customerEmail` VARCHAR(255)  NOT NULL, `customerPhone` VARCHAR(255)  NOT NULL, `customerCountry` VARCHAR(255)  NOT NULL, `customerType` VARCHAR(255)  NOT NULL, `customerTags` VARCHAR(255)  NOT NULL, `customerUpdated` TIMESTAMP  NOT NULL, `customerIdentify` VARCHAR(255)  NOT NULL)