CREATE TABLE iprojects (`projectId` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `projectContinent` VARCHAR(255)  NOT NULL CHECK (LENGTH(projectContinent) <= 100), `projectFlagCode` VARCHAR(255)  NOT NULL CHECK (LENGTH(projectFlagCode) <= 10), `projectImg` VARCHAR(255)  NOT NULL CHECK (LENGTH(projectImg) <= 100), `iprojectUpdated` TIMESTAMP  NOT NULL, `iprojectIdentify` VARCHAR(255)  NOT NULL)