CREATE TABLE metaseo (`id` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `pageSlug` VARCHAR(255)  NOT NULL, `metaTitle` VARCHAR(255)  NOT NULL, `metaDescription` VARCHAR(255)  NOT NULL, `metaKeywords` VARCHAR(255)  NOT NULL, `imageFeature` VARCHAR(255)  NOT NULL, `imageAlt` VARCHAR(255)  NOT NULL, `imageCaption` VARCHAR(255)  NOT NULL, `canonicalURL` VARCHAR(255)  NOT NULL, `metaAuthor` VARCHAR(255)  NOT NULL, `metaseoUpdated` TIMESTAMP  NOT NULL, `metaseoIdentify` VARCHAR(255)  NOT NULL)