CREATE TABLE networks (`networkId` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `networkCountry` VARCHAR(255)  NOT NULL CHECK (LENGTH(networkCountry) <= 255), `networkCity` VARCHAR(255)  NOT NULL CHECK (LENGTH(networkCity) <= 255), `networkStore` VARCHAR(255)  NOT NULL CHECK (LENGTH(networkStore) <= 255), `networkAddress` VARCHAR(255)  NOT NULL CHECK (LENGTH(networkAddress) <= 255), `networkImage` VARCHAR(255)  NOT NULL CHECK (LENGTH(networkImage) <= 255), `networkUpdated` TIMESTAMP  NOT NULL, `networkIdentify` VARCHAR(255)  NOT NULL)