CREATE TABLE portfolios (`portfolioId` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `portfolioName` VARCHAR(255)  NOT NULL, `portfolioURL` VARCHAR(255)  NOT NULL, `portfolioCountry` VARCHAR(255)  NOT NULL, `portfolioSummery` VARCHAR(255)  NOT NULL, `portfolioSolution` VARCHAR(255)  NOT NULL, `portfolioResult` VARCHAR(255)  NOT NULL, `portfolioTechnology` VARCHAR(255)  NOT NULL, `portfolioTag` VARCHAR(255)  NOT NULL, `portfolioUpdated` TIMESTAMP  NOT NULL, `portfolioIdentify` VARCHAR(255)  NOT NULL)