CREATE TABLE products (`productId` INT PRIMARY KEY NOT NULL, `productName` VARCHAR(255)  NOT NULL CHECK (LENGTH(productName) <= 255), `productDesc` VARCHAR(255)  NOT NULL CHECK (LENGTH(productDesc) <= 5000), `productPrice` VARCHAR(255)  NOT NULL CHECK (LENGTH(productPrice) <= 255), `productImage` VARCHAR(255)  NOT NULL CHECK (LENGTH(productImage) <= 255), `productTags` VARCHAR(255)  NOT NULL CHECK (LENGTH(productTags) <= 255), `productUpdated` TIMESTAMP  NOT NULL, `productIdentify` VARCHAR(255)  NOT NULL)