CREATE TABLE projects (`projectId` INT PRIMARY KEY NOT NULL AUTO_INCREMENT, `projectFlagCode` VARCHAR(255)  NOT NULL CHECK (LENGTH(projectFlagCode) <= 255), `projectImage` VARCHAR(255)  NOT NULL CHECK (LENGTH(projectImage) <= 255), `projectCountryName` VARCHAR(255)  NOT NULL CHECK (LENGTH(projectCountryName) <= 255), `projectUpdated` TIMESTAMP  NOT NULL, `projectIdentify` VARCHAR(255)  NOT NULL)